import React from 'react';
import { Menu, Icon, Input, Typography, Row, Col, Avatar } from 'antd';
import styled from 'styled-components';

const { Title, Paragraph, Text } = Typography;

const {Item} = Menu;
const { Search } = Input;



const MainTitle = styled(Typography)`
    color: #ffffff;
    font-size:30px;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const HomeIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const BellIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const SettingIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const UserIcon = styled(Avatar)`
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const MenuItem = {
    textAlign:'center'
}


const AppTopbar = () => {

    return(
        <div>
          <Menu mode="horizontal" selectedKeys='home' style={{backgroundColor:'#0B4E92'}} >
            <Row>
            <Col span={2}>
                <Item key="home" disabled={true} style={MenuItem} active="false">
                    <Icon type="home" />
                </Item>
            </Col>
            <Col span={4}>
                <Item key="search" disabled={true} style={MenuItem} active="false">
                    <Search
                        placeholder="input search text"
                        style={{ width: 200 }}
                    /> 
                </Item>
            </Col>
            <Col span={12}>
                <Item key="subject" disabled={true} style={MenuItem} active="false">
                    <Typography>
                        <MainTitle>RemindFeedBack</MainTitle>
                    </Typography>
                </Item>
            </Col>
            <Col span={3}></Col>
            <Col span={1}>
                <Item key="bell" disabled={true} style={MenuItem} active="false">
                    <BellIcon type="bell" />
                </Item>
            </Col>
            <Col span={1}>
                <Item key="user" disabled={true} style={MenuItem} active="false">
                    <UserIcon icon="user" />
                </Item>
            </Col>
            <Col span={1}>
                <Item key="setting" disabled={true} style={MenuItem} active="false">
                    <SettingIcon type="setting" />
                </Item>
            </Col>
            </Row>
          </Menu>  
        </div>
    );
};

export default AppTopbar;
