import React from 'react';
import { Menu, Icon, Input, Typography, Row, Col, Avatar } from 'antd';
import styled from 'styled-components';

const { Title, Paragraph, Text } = Typography;


const { Search } = Input;



const MainTitle = styled(Typography)`
    color: #ffffff;
    font-size:30px;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const HomeIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const BellIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const SettingIcon = styled(Icon)`
    color: #ffffff;
    font-size:25px;
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const UserIcon = styled(Avatar)`
    text-align: center;
    top:0;
    left:0;
    right:0;
    bottom:0;
`;

const MenuItem = {
    textAlign:'center'
}


const AppTopbar = () => {

    return(
        <div>
          <Menu mode="horizontal"  style={{backgroundColor:'#0B4E92'}} >
            {/* <Row> */}
            {/* <Col span={2}> */}
                <Menu.Item key="home"   >
                    <HomeIcon type="home" />
                </Menu.Item>
            {/* </Col> */}
            {/* <Col span={4}> */}
                <Menu.Item key="search" >
                    <Search
                        placeholder="input search text"
                        style={{ width: 200 }}
                    /> 
                </Menu.Item>
            {/* </Col> */}
            {/* <Col span={12}> */}
                <Menu.Item key="subject" >
                    <Typography>
                        <Title>RemindFeedBack</Title>
                    </Typography>
                </Menu.Item>
            {/* </Col> */}
            {/* <Col span={3}></Col>
            <Col span={1}> */}
                <Menu.Item key="bell"  >
                    <Icon type="bell" />
                </Menu.Item>
            {/* </Col> */}
            {/* <Col span={1}> */}
                <Menu.Item key="user" >
                    <Avatar icon="user" />
                </Menu.Item>
            {/* </Col> */}
            {/* <Col span={1}> */}
                <Menu.Item key="setting"   >
                    <Icon type="setting" />
                </Menu.Item>
            {/* </Col> */}
            {/* </Row> */}
          </Menu>  
        </div>
    );
};

export default AppTopbar;
